CPCJS.CHR$ = function(index){
	if(CPCJS._initialized !== true){
		CPCJS._error('CPCJS not initialized before CLS.');
		return;
	}

	if(index < 0 || index > 255){
		CPCJS._error('Impropred argument in CHR$ statement');
		return;
	}
	
	if(index > 31){
		return CPCJS._chr$[index - 32];
	}
}

CPCJS._reset_chr$ = function(){
	CPCJS._chr$ = CPCJS._system_characters.slice(0);
	return true;
}

CPCJS._reset_chr$();
